import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.text.SimpleDateFormat
import java.time.Instant
import java.time.ZoneOffset
import java.time.format.DateTimeFormatter

flowFile = session.get()
if (!flowFile) return


def formatDateTime(String inputDate) {
    if (inputDate?.length() == 14) { // Data X datetime format: yyyyMMddhhmmss
        def sdfInput = new SimpleDateFormat("yyyyMMddHHmmss")
        def sdfOutput = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss")
        return sdfOutput.format(sdfInput.parse(inputDate))
    } else if (inputDate?.isNumber()) { // Data Y datetime format: Unix timestamp
        def date = new Date(inputDate.toLong() * 1000)
        def sdfOutput = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss")
        return sdfOutput.format(date)
    }
    return null 
}


try {
    def inputJson

    session.read(flowFile, { inputStream ->
        def slurper = new JsonSlurper()
        inputJson = slurper.parse(inputStream)
    } as InputStreamCallback)

    def alarmCodeMapping = [
        "0"   : "0",
        "108" : "1",
        "109" : "2",
        "110" : "3",
        "111" : "4",
        "112" : "5"
    ]

    def outputJson = [:]

    if (inputJson.containsKey("IMEI") && inputJson.containsKey("yyyyMMddhhmmss")) {
        // Data X mapping
        outputJson = [
            imei                : inputJson.IMEI.toLong(),
            datetime            : formatDateTime(inputJson.yyyyMMddhhmmss.toString()),
            lng                 : inputJson.lon?.toBigDecimal(),
            lat                 : inputJson.lat?.toBigDecimal(),
            speed_mph           : inputJson.speed?.toDouble() * 0.621371, 
            altitude            : inputJson.alt ?: null,
            direction           : inputJson.dir ?: null,
            alarmCode           : alarmCodeMapping[inputJson.alarmCode.toString()].toLong(),
            Odometer            : null,
            LocationDesc        : null,
            RoadTypeDescription : null
        ]
    } else if (inputJson.containsKey("imei") && inputJson.containsKey("gps_time")) {
        // Data Y mapping
        outputJson = [
            imei                : inputJson.imei.toLong(),
            datetime            : formatDateTime(inputJson.gps_time),
            lng                 : inputJson.lng?.toBigDecimal(),
            lat                 : inputJson.lat?.toBigDecimal(),
            speed_mph           : inputJson.speed?.toDouble(), 
            altitude            : null,
            direction           : null,
            alarmCode           : inputJson.alarmcode.toLong(),
            Odometer            : inputJson.Odometer?.toBigDecimal() ?: null,
            LocationDesc        : inputJson.LocationDesc ?: null,
            RoadTypeDescription : inputJson.RoadTypeDescription ?: null
        ]
    } else {
        throw new Exception("Unrecognized data format!")
    }

    flowFile = session.write(flowFile, { outStream ->
        outStream.write(JsonOutput.toJson(outputJson).bytes)
    } as OutputStreamCallback)

    session.transfer(flowFile, REL_SUCCESS)
} catch (Exception e) {
    log.error("Error processing FlowFile: ${e.message}", e)
    session.transfer(flowFile, REL_FAILURE)
}
